import React, {  useEffect, useRef, useState } from "react";
import classes from "./in.module.css"
import { useNavigate } from "react-router-dom";


const In = (props) => {
    const password = useRef();
    const passwordConfirm = useRef();
    let [spano, setspano] = useState(true);
    const Handleachange = () => {
        if (password.current.value === passwordConfirm.current.value) {
            setspano(false);
        } else {
            setspano(true);
        }
        useEffect(() => {
            if (password.value === passwordConfirm.value) {
                    setspano("");
            } else {
                    setspano("Password doesnot match");
            }
            return(
                spano
            );
        }, [passwordConfirm])
    }

    const re_enter = <><label htmlFor='title'>Re-Enter Password</label>

        <input type="text" id="title" className={classes.control} required ref={passwordConfirm} />
        <span onChange={Handleachange} >{spano ? "doesnot match" : ""}</span>
    </>
    //         const link1=()=>{
    //  <Link to={props.linking}></Link>
    //         }

    let history = useNavigate();
    const interchangePage = () => {
        history(props.linking);
    }
    let history1 = useNavigate();
    const interchangePage1 = () => {
        history1(props.linking1);
    }
    return (


        <div className={classes.form}>
            <form >
                <h1>{props.TITLE}</h1>
                <div className={classes.imagecontainer}>
                    <img alt={props.TITLE} src={props.image} className={classes.avatar}>
                    </img>
                </div>
                <div className={classes.control}>

                    <label htmlFor='title'>UserName</label>

                    <input type="text" id="title" className={classes.control} required />

                    <label htmlFor='title'>Password</label>

                    <input type="password" id="title" className={classes.control} required ref={password}  />
                    {props.linking === '/' ? re_enter : ""}

                </div>
                <div className={classes.actions}>
                    <button onClick={interchangePage1}>{props.TITLE}</button>
                </div>
            </form>
            <div className={classes.actions}>
                <button onClick={interchangePage} >{props.button}</button>
            </div>
        </div>
    );
}
export default In;
